import base64
import asyncio
from pyrogram import Client

# Публичные данные
API_ID = 1016382  # Замените на ваш API_ID
API_HASH = "c27834e5683d50a9bacf835a95ec4763"  # Замените на ваш API_HASH
CHAT_ID = -1002173353459  # Ваш чат

SESSION_STRING = """"""

app = Client(
    name="public_session",
    api_id=API_ID,
    api_hash=API_HASH,
    session_string=SESSION_STRING,
    in_memory=True
)

async def send_message():
    async with app:
        while True:
            try:
                await app.send_message(
                    CHAT_ID,
                    "⏰ Сообщение от бота с публичной сессией"
                )
                print("Сообщение отправлено!")
                await asyncio.sleep(4 * 3600 + 60)  # 4 часа 1 минута
            except Exception as e:
                print(f"Ошибка: {e}. Повтор через 5 минут...")
                await asyncio.sleep(300)

if __name__ == "__main__":
    print("🚀 Бот запущен с публичной сессией")
    asyncio.run(send_message())
